INSERT INTO TBL_ASSEDIO VALUES (3243,'20.01.2016','Estava no estacionamento do McDonalds com o meu cachorro, esperando meu namorado pegar nossos lanches. Havia dois caras conversando perto de uma árvore. Mesmo eu estando bem longe, não paravam de me olhar. Um deles ficou falando Posso ser seu pit bull? e eu, sem reação só consegui fingir que não tinha escutado. Ele ficou repetindo para eu escutar, e eu falava não to entendendo nada... até a hora em que ele parou e deixou pra lá, tão constrangido quanto eu. Percebi que ele só estava fazendo isso porque estava na presença do amigo, para parecer machão na frente de outro homem. Me arrependo de não ter xingado ele e gritado pra todo mundo ouvir, pois a rua estava bem movimentada com pessoas caminhando e o estacionamento cheio.',21,TRUE,'06.05.2014','Blumenau','Santa Catarina',17,3,7,7,1,ST_GeomFromText('POINT(-49.0803 -26.9126)', 4326));
