INSERT INTO TBL_ASSEDIO VALUES (3278,'01.08.2013','Com tentativa de morte. Um homem aproximadamente de 50 e poucos anos, 1,60 de altura, cabelo grisalho, pele branca para morena, um pouco gordo, mas nem muito magro. Puxou-me para uma moita e dizendo vou te matar, vou te matar me enforcava e tentava me matar. Ele após bater-me muito, levou meu ipod junto com ele e saiu correndo com ipod como se fosse um corredor. Até hoje tenho vontade de tacar fogo naquela moita, que é próxima aqueles aparelhos de madeira para exercicio fisico de abdomem. Era umas 18h, 19h. Sempre passo por ali e tenho vontade de encontrá-lo para arrancar o pinto dele e enfiar uma vassoura no cu até ele morrer, pois ele pode estar fazendo isso com outras. Todo homem que abusa de uma mulher, criança, homem, etc deve apanhar muito, sofrer até morrer. Obrigada.',26,TRUE,'06.05.2014','São Paulo','São Paulo',19,2,7,7,1,ST_GeomFromText('POINT(-46.6429 -23.5065)', 4326));
