INSERT INTO TBL_ASSEDIO VALUES (3322,'18.08.2016','Estava caminhando em direção a minha casa com um amigo, quando tivemos que passar por um grupo com uns 5 meninos que estavam na frente de uma lan house, quando tinhamos acabado de passar percebi que algo molhado tinha acabado de cair em mim, olhei pra tras e um cara estava fazendo um movimento muito estranho atras do meu amigo, depois eu percebi que o menino tinha guspido em mim, nojento.',17,TRUE,'05.05.2014','Chapecó','Santa Catarina',27,2,7,6,1,ST_GeomFromText('POINT(-52.6107 -27.1101)', 4326));
