INSERT INTO TBL_ASSEDIO VALUES (3382,'13.02.2016','Querendo aproveitar o carnaval com meus amigos decidi fazer uma social na área de lazer do meu prédio. Todos chegaram e do meu ap fomos levar as bebidas e o som pra a área que eu tinha reservado com antecedência. Com o passar do tempo chegaram uns caras estranhos e ficaram do lado da cerca que separava o ambiente reservado do condomínio do público. Como os banheiros do lugar ficavam fechados esse emu amigo foi ao meu apartamento. Na passagem ele foi chamado de viadinho, bixinha, e outras ofensas mais e fingiu que não ouviu e seguiu. Na volta um dos caras pegou ele pelo pescoço, inventou uma história para iniciar a agressão e pegou meu amigo pelo pescoço, disse que ia matar ele e todos os viados que tinham na festa, que ninguém saia vivo. Foi um dos piores dias, a festa a acabou e eu me mudei pra nunca mais voltar.',17,TRUE,'01.05.2014','false','Paraíba',21,3,11,0,1,ST_GeomFromText('POINT(-34.8461 -7.08287)', 4326));
