INSERT INTO TBL_ASSEDIO VALUES (3423,'12.11.2016','Estava à caminho da academia, quando um cara de uns 40 e poucos anos se aproximou e me chamou de  gostosa e ficou me olhando dos pés a cabeça. Sempre fico receosa ao andar com roupas de academia na rua, muitas das vezes troco de roupa antes de sair da academia. ELES PRECISAM PARAR, CHEGA!!!',22,TRUE,'30.04.2014','Rio de Janeiro','Rio de Janeiro',17,0,11,7,6,ST_GeomFromText('POINT(-43.4652 -22.8796)', 4326));
