INSERT INTO TBL_ASSEDIO VALUES (3449,'24.12.2016','O homem parou o carro, enquanto eu andava pela calçada e disse bom dia. Como o tom de voz já mostrava que se tratava de assedio, eu não parei e andei mais rápido. Ele me seguiu com o carro na mesma velocidade que eu andava. Parei e disse com voz antipática: bom dia. Ele continuou o assédio e fez menção de me seguir de novo caso eu continuasse andando. Eu disse: eu não posso andar na rua não? Ele respondeu com ironia que sim. Eu disse: não com alguém me seguindo! Aí ele resolveu ir embora. Eu idiota, não anotei a placa do carro do assediador.',34,TRUE,'29.04.2014','São Paulo','São Paulo',17,1,7,3,6,ST_GeomFromText('POINT(-46.6793 -23.4655)', 4326));
