INSERT INTO TBL_ASSEDIO VALUES (3489,'01.02.2002','Eu tinha 13 anos de idade e estava voltando de uma sorveteria com uma amiga a pé. Um homem com aparência de morador de rua caminhava na direção oposta de nós duas. Quando passou por nós, ele apalpou a minha coxa e continuou seguindo. Fiquei apavorada e falei O que é isso?!. Minha amiga me disse Tá vendo... você fica usando essa sainha desse tamanho!. Não soube o que responder, apenas sentia que de alguma forma tinha sido minha culpa.',28,TRUE,'27.04.2014','Votuporanga','São Paulo',16,2,7,4,1,ST_GeomFromText('POINT(-49.9724 -20.4197)', 4326));
