INSERT INTO TBL_ASSEDIO VALUES (3504,'18.02.2017','Saímos em direção à um evento que aconteceria na escola, nos arrumamos com o intuito de ficarmos lindas pra nós mesmas... Na ida para o ponto de ônibus, fomos assediadas por uns 6 homens, alguns de carro, moto e até mesmo a pé... Ao chegar no terminal, um homem começou a nos assediar abusivamente, chegou a vir para perto do meu ouvido e falar o como eu estava gostosa. Eu senti um nojo tão grande, e parei pra pensar como eles acham que a gente se sente? Acham que gostamos? Até quando vamos SAIR amedrontadas de nossas casas? Basta! ',16,TRUE,'26.04.2014','Aracaju','Sergipe',17,1,11,0,2,ST_GeomFromText('POINT(-37.0728 -10.9473)', 4326));
