INSERT INTO TBL_ASSEDIO VALUES (3581,'10.03.2017','No meio da aula, brinquei durante 2/3 dias com um amigo que a gente ficaria, mas ele sabia que eu nao estava falando sério porque eu namoro. Ele começou a insistir muito e me segurar pelo cabelo e belas mãos durante as falas dele, que eu tentava ignorar/dizer que nao ficaria com ele de jeito nenhum. Até que um dia meus amigos estavam falando sobre sensibilidade no corpo por causa do seminário e ele pegou na parte interior na minha perna, perto da virilha enquanto mordia os lábios. Fiquei muito assustada e chorei bastante nesse dia',15,TRUE,'25.04.2014','São Paulo','São Paulo',16,0,11,6,1,ST_GeomFromText('POINT(-46.5672 -23.5419)', 4326));
INSERT INTO TBL_ASSEDIO VALUES (3582,'09.02.2018','Estava numa festa de aniversário de uma amiga e bêbada, quando um menino com quem ja me relacionei antes chegou em mim e nós ficamos, ai ele me levou pro banheiro do prédio e quando me dei por mim,  ele ja tinha gozado dentro de mim e no meu corpo e roupas, e estava saindo de lá, me deixando completamente sozinha e com vergonha. Chorei muito e uma amiga comprou uma pds e sangrou, o que me abalou mais porque se eu nao tivesse tomado, teria engravidado dele. Até hoje, ele é um gatilho pra mim e eu passo mal na presença dele.',15,TRUE,'25.04.2014','false','Minas Gerais',19,0,11,7,6,ST_GeomFromText('POINT(-46.7907 -17.57)', 4326));
