INSERT INTO TBL_ASSEDIO VALUES (3689,'14.09.2007','Estava passando pela praça central do shopping, quando cruzei com um cara muito mais velho que eu. Nessa fração de segundos em que cruzamos, escutei num sussurro, mas muito claro, aquele gostosa...  Não tive tempo de reagir e o cara já havia sumido. Tive tempo apenas de seguir o meu caminho e balançar a cabeça em sinal de desaprovação... eu comigo mesma. Tinha 18 na época.',28,TRUE,'24.04.2014','Brasília','Distrito Federal',17,2,11,7,1,ST_GeomFromText('POINT(-47.892 -15.7961)', 4326));
