INSERT INTO TBL_ASSEDIO VALUES (3694,'15.09.2017','Pego o trem todos os dias para ir trabalhar, não uso roupas chamativas e já sofri assédio de homens que ficam se esfregando. Fico tão horrorizada que não consigo ter reação e devido ao trem estar muito cheio não consigo sair de perto até esvaziar. Sinto uma agonia enorme por não conseguir fazer nada.',34,TRUE,'24.04.2014','Nova Iguaçu','Rio de Janeiro',27,1,7,2,1,ST_GeomFromText('POINT(-43.45 -22.7608)', 4326));
