INSERT INTO TBL_ASSEDIO VALUES (3699,'17.09.2017','Estava no saguão do aeroporto quando fui abordada por um vendedor de assinatura de revistas. Ele falava alto pra que eu chegasse mais perto e pegasse um panfleto. Não quis ser mal educada, me aproximei e peguei. Ele continuou sendo insistente pra que eu dissesse qual a bandeira do meu cartão de crédito, pra que eu participasse de alguma promoção. Eu então mencionei que não possuía cartão de crédito. Ele então respondeu: Oh dó, tá de castigo, tiraram o seu cartão de crédito?',31,TRUE,'24.04.2014','São José dos Pinhais','Paraná',17,2,7,3,1,ST_GeomFromText('POINT(-49.1787 -25.534)', 4326));
