INSERT INTO TBL_ASSEDIO VALUES (3710,'04.10.2002','Estava voltado do curso e pelo horário o ônibus estava relativamente vazio, alguns pontos depois subiu um rapaz e mesmo tendo lugares para sentar veio em minha direção e ficou ao meu lado, assim q ele ficou ao meu lado pudi senti algo quente em minha perna mesmo estando de calça, quando olhei a ponchete que ele usava estava meio de lado, entrei em desespero interno não consegui fazer absolutamente nada, só tenta afasta a perna e ele continuava vindo para mais perto,mesmo tendo outra pessoa ao meu lado ele não se intimidou, até que alguns minutos depois dele desceu. E eu permaneci imóvel. Calada.',20,TRUE,'24.04.2014','Maceió','Alagoas',16,1,11,7,1,ST_GeomFromText('POINT(-35.7382 -9.62691)', 4326));
INSERT INTO TBL_ASSEDIO VALUES (3711,'11.07.2017','Eu estava voltando da padaria quando um homem me abordou e tentou me puxar, eu me soltei e sai correndo... porém ele disse que um dia ele vai me pegar  ',13,TRUE,'24.04.2014','Macapá','Amapá',15,3,6,7,3,ST_GeomFromText('POINT(-51.0841 0.0945652)', 4326));
