INSERT INTO TBL_ASSEDIO VALUES (3755,'20.09.2017','Estava andando ao lado da integração de ônibus quando um carro, na outra faixa, desacelerou e ficou me acompanhando. Baixou os vidros e ficou chamando minha atenção com o famoso psiu. Pensei em voltar até onde tinham alguns ambulantes na rua. Até que ele não conseguiu atravessar a faixa e foi embora. ',25,FALSE,'23.04.2014','Campina Grande','Paraíba',17,3,7,7,1,ST_GeomFromText('POINT(-35.8896 -7.2203)', 4326));
