INSERT INTO TBL_ASSEDIO VALUES (3767,'01.11.2007','Eu tinha 13 anos e estava caminhando, voltando da escola. Em sentido oposto ao meu vinha um homem de bicicleta que reduziu a velocidade e perguntou a hora. Antes que eu pudesse responder qualquer coisa ele puxou suas partes íntimas para fora do short que usava e disse: olha. Virei o rosto e saí em passo apressado. Olhei para trás pouco tempo depois e ele havia sumido.',13,TRUE,'23.04.2014','Campina Grande','Paraíba',27,2,5,2,2,ST_GeomFromText('POINT(-35.8893 -7.25118)', 4326));
