INSERT INTO TBL_ASSEDIO VALUES (3845,'08.02.2018','Estava andando quando um cara com uns 40 anos me parou e falou coisas indecentes (prefiro não relembrar exatamente oq), começou a se esfregar, me tocar, enfim, fiquei sem reação, com medo, me sentindo invadida e tão suja... imunda... queria arrancar a minha própria pele para não restar mais nada, nenhuma lembrança dele',18,TRUE,'22.04.2014','Franco da Rocha','São Paulo',16,0,11,7,6,ST_GeomFromText('POINT(-46.7344 -23.3158)', 4326));
