INSERT INTO TBL_ASSEDIO VALUES (3847,'12.02.2018','Ia sempre para o supermercado, as vezes um idoso com mais de 60 anos, de olhos claros, me abordava no caminho sempre muito simpático, me perguntava as horas e enredava algum assunto... desde a primeira vez q o vi senti um pressentimento estranho, mas pensei não, deve ser coisa da minha cabeça, estou vendo maldade onde não tem, afinal de contas, é só um velhinho querendo conversar.... Depois de um tempo descobri, conversando com pessoas q moram no bairro a mais tempo, q ele costuma ter segundas intenções com garotas. Agr faço outro caminho para as compras',18,TRUE,'22.04.2014','Jundiaí','São Paulo',17,0,11,7,6,ST_GeomFromText('POINT(-46.9116 -23.1701)', 4326));
