INSERT INTO TBL_ASSEDIO VALUES (3882,'14.10.2011','Eu estava saindo da UECE (Campus Fátima) e passava por uma rua que dá acesso à Av. 13 de maio para pegar meu ônibus de volta pra casa... Em frente à Escola pública Adauto Bezerra, um carro estava estacionado e o homem no volante me chamou como querendo pedir informação. Me aproximei um pouco do carro quando me deparei com una cena asquerosa: uma mulher fazendo oral nele enquanto ele olhava as pessoas passando na calçada da ESCOLA! Fiquei muito assustada e nervosa, saí correndo em direção à avenida e só deixei de tremer quando cheguei em casa. A partir desse dia, meu pai ia me buscar diariamente da faculdade. ',27,FALSE,'22.04.2014','Fortaleza','Ceará',18,3,8,3,2,ST_GeomFromText('POINT(-38.5312 -3.75037)', 4326));
