INSERT INTO TBL_ASSEDIO VALUES (3910,'04.04.2018','Um dia esqueci de levar a chave de casa voltei da faculdade e fiquei na rua. Resolvi pegar uma copia da chave com a minha mãe na igreja. Voltando já na rua da minha casa com pouca iluminação um homem no carro para o carro ao meu lado mostrando uma arma e mandando eu entrar. Fiquei paralisada, sem reação. O cara gritando loucamente foi então que tive a ideia de correr e despista-lo e ele foi embora. Decorei a placa do carro dele que sei até hoje. Dei para a minha ela foi no Detran do Maranhão e pediu para uma amiga reastrear o nome do dono e endereço. Para nossa surpresa o meu agressor mora quatro ruas após a minha e já o vi em uma igreja perto de minha casa. ',23,TRUE,'22.04.2014','São Luís','Maranhão',15,3,11,0,6,ST_GeomFromText('POINT(-44.1981 -2.57713)', 4326));
